#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="D:\Dev\Spielwiese\ELM\bootstrap\node_modules\.pnpm\elm@0.19.1-5\node_modules\elm\bin\node_modules;D:\Dev\Spielwiese\ELM\bootstrap\node_modules\.pnpm\elm@0.19.1-5\node_modules\elm\node_modules;D:\Dev\Spielwiese\ELM\bootstrap\node_modules\.pnpm\elm@0.19.1-5\node_modules;D:\Dev\Spielwiese\ELM\bootstrap\node_modules\.pnpm\node_modules;D:\Dev\Spielwiese\ELM\bootstrap\node_modules;D:\Dev\Spielwiese\ELM\node_modules;D:\Dev\Spielwiese\node_modules;D:\Dev\node_modules;D:\node_modules;D:\Dev\Spielwiese\ELM\bootstrap\node_modules\elm\bin\node_modules;D:\Dev\Spielwiese\ELM\bootstrap\node_modules\elm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/Dev/Spielwiese/ELM/bootstrap/node_modules/.pnpm/elm@0.19.1-5/node_modules/elm/bin/node_modules:/mnt/d/Dev/Spielwiese/ELM/bootstrap/node_modules/.pnpm/elm@0.19.1-5/node_modules/elm/node_modules:/mnt/d/Dev/Spielwiese/ELM/bootstrap/node_modules/.pnpm/elm@0.19.1-5/node_modules:/mnt/d/Dev/Spielwiese/ELM/bootstrap/node_modules/.pnpm/node_modules:/mnt/d/Dev/Spielwiese/ELM/bootstrap/node_modules:/mnt/d/Dev/Spielwiese/ELM/node_modules:/mnt/d/Dev/Spielwiese/node_modules:/mnt/d/Dev/node_modules:/mnt/d/node_modules:/mnt/d/Dev/Spielwiese/ELM/bootstrap/node_modules/elm/bin/node_modules:/mnt/d/Dev/Spielwiese/ELM/bootstrap/node_modules/elm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$env_node_path$pathsep$new_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../elm/bin/elm" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../elm/bin/elm" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../elm/bin/elm" $args
  } else {
    & "node$exe"  "$basedir/../elm/bin/elm" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
